package jcurses.widgets;import jcurses.widgets.window.WindowManager;/** * <code>WidgetUtilities</code> contains utility methods for the jcurses library. * * @author <a href="mailto:lenbok@myrealbox.com">Len Trigg</a> * @version $Revision: 1.1 $ */public class WidgetUtilities {	/**	 * Method to be used by external threads wishing to perform	 * <p>	 * safe calls to jcurses widgets. A typical jcurses	 * <p>	 * application only requires the built-in jcurses input thread	 * <p>	 * and need not use this method.	 *	 * @param r a <code>Runnable</code> containing the code to be	 *          <p>	 *          executed in a thread-safe manner.	 */	public static void invokeAndWait(Runnable r) {		WindowManager.invokeAndWait(r);	}}